document.addEventListener("DOMContentLoaded", function(event) {
	"use strict";

	// Page scroll navigation
	// init elements and variables
	var page_segments = document.querySelectorAll('.page-segment'),
		page_nav = document.querySelector('.page-nav__list'),
		content_section, 
		page_nav_item, 
		segment_position, 
		top_current,
		offset = -100;


	// generate page nav body
	for (var i = 0; i < page_segments.length; i++){
		page_nav_item = page_segments[i];

		if( page_nav_item.classList.contains('card--product') ){
			page_nav.innerHTML += '<li class="page-nav__item page-nav__item--product"><a data-scroll href="#' + page_nav_item.id + '"">' + page_nav_item.dataset.name + '</a></li>';
		} else {
			content_section = getClosest(page_nav_item, '.content-section');

			if( content_section.classList.contains('content-section--include') ){
				page_nav.innerHTML += '<li class="page-nav__group">' + page_nav_item.firstChild.textContent + '</li>';
			} else {
				page_nav.innerHTML += '<li class="page-nav__item"><a data-scroll href="#' + content_section.id + '"">' + page_nav_item.firstChild.textContent + '</a></li>';
			}
		}
	}


	// calculate and highlight current segment
	function current_segment(){
		var find_active = false;

		for (var j = 0; j < page_nav.children.length; j++){
			// - clear active styling class
			page_nav.children[j].classList.remove('active');

			// - find segments position 
			segment_position = page_segments[j].getBoundingClientRect();
			top_current = segment_position.top + offset;

			// - add active styling class for suitable segment indicator
			if (!find_active){
					if ( (j == 0 && top_current > 0) || (j == (page_nav.children.length - 1) && top_current < 0) ) {
					page_nav.children[j].classList.add('active');
					find_active = true;
				} else if (top_current > 0) {
					page_nav.children[j - 1].classList.add('active');
					find_active = true;
				}
			}
		}
	}


	// define contols (prev, next and toggle) and nav links behaviour on click
	var section_prev = document.querySelector('.page-nav__prev'),
		section_next = document.querySelector('.page-nav__next'),
		page_nav_toggle = document.querySelector('.page-nav__toggle');

	var page_nav_wrap = document.querySelector('.page-nav'),
		page_nav_items = document.querySelectorAll('.page-nav__item'),
		page_nav_link = document.querySelectorAll('.page-nav__item a');

	// - prev section
	var sectionPrev = function(e) {
		e.preventDefault();

		for (var k = 0; k < page_nav_items.length; k++){
			if( page_nav_items[k].classList.contains('active') && k > 0){
				page_nav_items[k - 1].firstChild.click();
			}
		}
	};

	section_prev.addEventListener('click', sectionPrev, false);

	// - next section
	var sectionNext = function(e) {
		e.preventDefault();

		for (var k = 0; k < page_nav_items.length; k++){
			if( page_nav_items[k].classList.contains('active') && k < page_nav_items.length - 1){
				page_nav_items[k + 1].firstChild.click();
			}
		}
	};

	section_next.addEventListener('click', sectionNext, false);

	// - page nav toggle
	var pageNavToggle = function(e) {
		e.preventDefault();

		page_nav_wrap.classList.toggle('reveal');
	};

	page_nav_toggle.addEventListener('click', pageNavToggle, false);

	// - page nav links
	for (var i = 0; i < page_nav_link.length; i++) {
	    page_nav_link[i].addEventListener('click', function(){
	    	page_nav_wrap.classList.remove('reveal');
	    }, false);
	}


	// define page navigation behaviour on scroll
	var timer;

	window.onscroll = function(event) {
    	if(timer) {
			window.clearTimeout(timer);
		}

		timer = window.setTimeout(function() {
			current_segment();
		}, 100);
	};

	current_segment();


	// Preview slider init
	var preview_slider = new Swiper('.preview-slider', {
	 	slidesPerView: 1,
	 	speed: 400,
		pagination: {
	        el: '.swiper-pagination',
	        clickable: true,
	    },
	    watchOverflow: true,
		simulateTouch: false,
		observer: true,
		observeParents: true,
		autoHeight: true
	});


	// Video start for target time
	var play_controls = document.querySelectorAll('.play-video');

	var playVideo = function(e) {
		e.preventDefault();

		var target_video = document.querySelector("[src*= " + this.dataset.target + "]");
		var symbol = target_video.dataset.src.indexOf("?") > -1 ? "&" : "?";
		var timestamp = toSecondsOnly(this.dataset.time);

  		target_video.src = target_video.dataset.src + symbol + "autoplay=1" + "&start=" + timestamp;
	};

	if ( play_controls ) {
		for (var i = 0; i < play_controls.length; i++) {
		    play_controls[i].addEventListener('click', playVideo, false);
		}
	}


	// Form interaction class handle
	var form_input = document.querySelectorAll('.form__input');

	var inputFocus = function() {
		var parent = getClosest(this, '.form__group');
		parent.classList.add('focus')
	};

	var inputBlur = function() {
		var parent = getClosest(this, '.form__group');
		parent.classList.remove('focus')
	};

	if ( form_input ) {
		for (var i = 0; i < form_input.length; i++) {
		    form_input[i].addEventListener('focus', inputFocus, false);
		}

		for (var i = 0; i < form_input.length; i++) {
		    form_input[i].addEventListener('blur', inputBlur, false);
		}
	}
});

// convert time in mm:ss or hh:mm:ss to simple value
function toSecondsOnly(time_format) {
	"use strict";

    var digit = time_format.split(':'),
        seconds = 0, multi = 1;

    while (digit.length > 0) {
        seconds += multi * parseInt(digit.pop(), 10);
        multi *= 60;
    }

    return seconds;
}

// helper function to find closest matches parents up the tree
var getClosest = function ( elem, selector ) {
	"use strict";

	// Element.matches() polyfill
	if (!Element.prototype.matches) {
		Element.prototype.matches =
			Element.prototype.matchesSelector ||
			Element.prototype.mozMatchesSelector ||
			Element.prototype.msMatchesSelector ||
			Element.prototype.oMatchesSelector ||
			Element.prototype.webkitMatchesSelector ||
			function(s) {
				var matches = (this.document || this.ownerDocument).querySelectorAll(s),
					i = matches.length;
				while (--i >= 0 && matches.item(i) !== this) {}
				return i > -1;
			};
	}

	// Get closest match
	for ( ; elem && elem !== document; elem = elem.parentNode ) {
		if ( elem.matches( selector ) ) return elem;
	}

	return null;
};
